package com.anStudios.bookingstats;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;

import java.util.ArrayList;

public class RecentBookings extends AppCompatActivity {

    private ArrayList<RecentModelClass> recentBookings;
    private RecentAdapter recentAdapter;
    private RecyclerView recentRecycler;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_recent_bookings);
        recentBookings=new ArrayList<>();
        recentBookings.add(new RecentModelClass("data:image/jpeg;base64,","Anshul Negi","45-32-4567","Chamba","Rishikesh"));
        recentAdapter=new RecentAdapter(recentBookings,this);
        recentRecycler=findViewById(R.id.recent_recyclerView);
        recentRecycler.setLayoutManager(new LinearLayoutManager(this));
        recentRecycler.setAdapter(recentAdapter);
    }
}